public int[] longitudArray(){
String input = JOptionPane.showInputDialog("Ingresa la longitud del array");
int n = Integer.parseInt(input);
int arr[] = new int[n];

JOptionPane.showMessageDialog(null, "Ingresa los elementos del array");        
 for (int i = 0; i < arr.length; i++) {
    input = JOptionPane.showInputDialog("Elemento "+ (i + 1)+ ": ");
    arr[i] =Integer.parseInt(input);
}
 return arr;
}
public void Seleccion(int[] arr){
    int n = arr.length;
    for (int i=0; i < n -1; i++) {
        int minIndex =i;
        for(int j= i + 1; j < n; j++){
            if (arr[j] < arr[minIndex]) {
                minIndex =j;
            }
        }
        int temp = arr[i];
        arr[i] = arr[minIndex];
        arr[minIndex] = temp;
    } 
} 
public void BurbujaOrd(int[]arr){
    int n = arr.length;
    for (int i = 0; i < n - 1; i++) {
        for (int j = 0; j < n - i - 1; j++){
            if (arr[j] > arr[j + 1]) {
                int temp = arr[j+1];
                        arr[j+1] = temp;
            }
        }
    }
}
public void mostrar(){
    for(int indice= 0; indice < variable_lista.length; indice++){
        
        JOptionPane.showMessageDialog(rootPane, variable_lista[indice]);
    }
}
public static String visualizar (int[] arr){
    StringBuilder sb = new StringBuilder();
    for(int i = 0; i < arr.length; i++){
        sb.append(arr[i]).append( " ");
    }
    return sb.toString();
    
}

